queries.nft_collection_history:
query HistoryCollectionSales($collectionAddress: String!, $first: Int!) {
  historyCollectionNftItems(collectionAddress: $collectionAddress, first: $first) {
    items {
      address
      collectionAddress
      time
      typeData {
        ... on HistoryTypeMint {
          type
          historyType
        }
        ... on HistoryTypeTransfer {
          type
          historyType
          oldOwner
          newOwner
        }
        ... on HistoryTypeCancelSale {
          type
          historyType
          owner
          price
        }
        ... on HistoryTypeSold {
          type
          historyType
          oldOwner
          newOwner
          price
          rejectFromGlobalTop
        }
        ... on HistoryTypePutUpForSale {
          type
          historyType
          owner
          price
        }
        ... on HistoryTypePutUpForAuction {
          type
          historyType
          owner
        }
        ... on HistoryTypeCancelAuction {
          type
          historyType
          owner
        }
        ... on HistoryTypeBurn {
          type
          historyType
          oldOwner
          newOwner
        }
      }
      nft {
        address
        name
      }
    }
  }
}

get_sale_info.native:
query Items($address: String!, $first: Int!) {
  reactionsNft(address: $address, first: $first) {
    nft {
      sale {
        ... on NftSaleFixPrice {
          address
          nftOwnerAddress
          fullPrice
          marketplaceFeeAddress
          marketplaceFee
          royaltyAddress
          royaltyAmount
          networkFee
          __typename
        }
        ... on NftSaleAuction {
          address
          nftOwnerAddress
          marketplaceFeeAddress
          marketplaceFeePercent
          royaltyAddress
          royaltyPercent
          minBid
          maxBid
          minStep
          stepIsPercent
          isCancelable
          minNextBid
          end
          finishAt
          lastBidAmount
          lastBidAmountWithFee
          lastBidAddress
          lastBidUser {
            wallet
            name
            lang
            telegram {
              hasTelegram
              userName
            }
            socialLinks {
              type
              url
            }
          }
          lastBidAt
          version
          networkFee
          __typename
        }
      }
    }
  }
}

