queries.get_nft_info:
query Items($address: String!, $first: Int!, $width: Int!, $height: Int!) {
  reactionsNft(address: $address, first: $first) {
    nft {
      id
      address
      owner {
        wallet
        name
        telegram {
          hasTelegram
          userName
        }
        socialLinks {
          type
          url
        }
        lang
      }
      collection {
        address
        name
        description
        owner {
          wallet
          name
          lang
          telegram {
            hasTelegram
            userName
          }
          socialLinks {
            url
            type
          }
        }
        socialLinks
        hasRarityAttributes
        image {
          image {
            baseUrl
            preview(width: $width, height: $height)
            sized(width: $width, height: $height)
          }
        }
        approximateItemsCount
        approximateHoldersCount
      }
      version
      index
      editorAddress
      name
      description
      content {
        ... on NftContentImage {
          image {
            baseUrl
            preview(width: $width, height: $height)
            sized(width: $width, height: $height)
          }
          originalUrl
        }
        ... on NftContentNotLoaded {
          notLoaded
        }
        ... on NftContentVideo {
          baseUrl
          preview(width: $width, height: $height)
          sized(width: $width, height: $height)
        }
      }
      attributes {
        displayType
        traitType
        value
      }
      metadataSourceType
      contentSourceType
      isApproved
      priority
      isBlocked
      reactionCounters {
        likes
      }
      rarityRank
      rarityAttributes {
        traitType
        value
        maxShapeCount
        rarityPercent
      }
      kind
      maxOffer {
        fullPrice
        profitPrice
        royaltyPrice
        feePrice
        isKnownMarketplaceFeeReceiver
        feeAddress
        finishAt
        user {
          wallet
          telegram {
            hasTelegram
            userName
          }
          socialLinks {
            type
            url
          }
          name
          lang
        }
        offerAddress
        royaltyAddress
      }
      isBurned
      isHiddenByUser
      domain
      colorScheme
      isRevealable
    }
  }
}

